mapper.select:
select u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.mobile, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.mobile like concat('%', #{mobile}, '%')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))

select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.mobile, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.mobile like concat('%', #{mobile}, '%')
